 import React, { Component } from 'react'
 import Grid from '@material-ui/core/Grid';
 import Box from '@material-ui/core/Box';
 import Button from '@material-ui/core/Button';
 import numeros_ejemplo from '../imgs/Logos_items/introduccion/numeros_ejemplo_1.png';

 class Inicio_mpt extends Component{
     render(){
        return (
 <body>
 <div style={{ padding: 20 }}>
        <u><h1 align ='center'> Bienvenid@ a Matemáticas para todos</h1></u>
        <h2> Para comenzar repasaremos cuales son los números como se puede ver en la siguiente imagen:</h2>
    <img src={numeros_ejemplo}width="100%" height= "180px" />
        <p> como podemos ver de los números "Básicos" son estos de izquierda a derecha "->"
        se encuentra el número 0, al lado derecho "->" el número 1 conocido como número Uno, a su lado el número 2 conocido como número Dos
         y así el número 3 conocido como número Tres, número 4 conocido como número Cuatro, número 5 conocido como número Cinco,
        número 6 conocido como número Seis, número 7 conocido como número Siete, número 8 conocido como número Ocho
        y finalmente el número 9 conocido como número Nueve.
        </p>
  </div>
 </body> 
         )
     }
 }
export default Inicio_mpt;
